function parseCity(content, cityList) {
    for (let i = 0; i < cityList.length; i++) {
        if (content.indexOf(cityList[i]) !== -1) {
            return cityList[i];
        }
    }
    return null;
}

function filterContentForTime(content) {
    let filters = ['高一', '高二', '高三'];
    let filters2 = ['周一', '周二', '周三', '周四', '周五', '周六'];
    let list = filters.concat(filters2);

    list.forEach(item => {
        content = content.replace(item, '');
    });
    return content;
}

function parseTime(contentBak) {
    content = filterContentForTime(contentBak);
    let timeRegexp = /[0123456789零一二三四五六七八九十]{1,2}[点时]{1}[0123456789零一二三四五六七八九十整]{0,3}[分]{0,1}/
    let timeRegexp2 = /[0123456789]{1,2}[:：]{1}\s{0,1}[0123456789]{2}/;
    let regexps = [timeRegexp, timeRegexp2];

    for (let i = 0; i < regexps.length; i++) {
        let reg = regexps[i];
        let arr = reg.exec(content);
        if (arr !== null && arr.length > 0) {
            let result = arr[0];
            result = result.trim().replace(/\s/g, '').replace(/：/g, ':');
            return result;
        }
    }
    return null;
}

