function request(url) {
    return new Promise((resolve, reject) => {
        var xhr = new XMLHttpRequest();
        xhr.onreadystatechange = ev => {
            if (xhr.status === 200 && xhr.readyState === 4) {
                resolve(xhr.responseText);
            }
        };

        xhr.open('GET', url);
        xhr.send();
    });
}


function getDBhandler(tableName, tableNameDatabase) {
    return new Promise((resolve, reject) => {
        var request = window.indexedDB.open(tableNameDatabase, 3);

        request.onerror = function (event) {
            // Do something with request.errorCode!
        };
        request.onsuccess = function (event) {
            db = event.target.result;
            // Create an objectStore for this database
            var tobj = db.transaction(tableName, 'readwrite').objectStore(tableName);
            resolve(tobj);
        };
        request.onupgradeneeded = function (event) {
            // Do something with request.result!
            db = event.target.result;
            // Create an objectStore for this database
            var objectStore = db.createObjectStore(tableName, {autoIncrement: true});

            objectStore.transaction.oncomplete = function (event) {
                // 将数据保存到新创建的对象仓库
                var tobj = db.transaction(tableName, 'readwrite').objectStore(tableName);
                resolve(tobj)
            };
        };
    });
}

function sleep(sleepMilliseconds) {
    return new Promise((resolve, reject) => {
        setTimeout(() => {
            resolve(true)
        }, sleepMilliseconds)
    });
}

